#!/usr/bin/env python# -*- coding: utf-8 -*-# @Time    : 2019/3/18 11:59# @Author  : ZhangChaoweifrom pynput.keyboard import Key, Controllerkeyboard = Controller()# 按下并释放空格keyboard.press(Key.space)keyboard.release(Key.space)# 按下并释放小写字母akeyboard.press('a')keyboard.release('a')# 2种方式输入大写Akeyboard.press('A')keyboard.release('A')with keyboard.pressed(Key.shift):    keyboard.press('a')    keyboard.release('a')# 直接操作键盘输入Hello Worldkeyboard.type('Hello World')