#!/usr/bin/env python# -*- coding: utf-8 -*-# @Time    : 2019/3/18 11:43# @Author  : ZhangChaoweifrom pynput import mousedef on_move(x, y):    print(f'鼠标移动到的坐标 {(x, y)}')def on_click(x, y, button, pressed):    print(f"{'按下' if pressed else '释放'}, 当前位置是：{(x, y)}")    if not pressed:        # 停止监听        return Falsedef on_scroll(x, y, dx, dy):    print(f"滑动鼠标，{'向下' if dy < 0 else '向上'} at {(x, y)}")with mouse.Listener(    on_move=on_move,    on_click=on_click,    on_scroll=on_scroll) as listener:    listener.join()